INSERT[dbo].[Speakers] ([Id], [FirstName], [LastName], [Biography], [PhotoUrl], [AvatarUrl], [PositionName], [CompanyName], [CompanyWebsiteUrl], [BlogUrl], [TwitterUrl], [LinkedInUrl], [Email], [RemoteId], [CreatedAt], [UpdatedAt], [Deleted]) VALUES(N'97E0A8E9-6090-4830-8E01-FC924E4ADB8C', N'Robin', N'Sedlaczek', N'Robin Sedlaczek hat mehr als fünfzehn Jahre Erfahrung als professioneller Softwareentwickler. Seit 7 Jahren tätig bei der Fairmas GmbH in Berlin, hat er das auf Finanzplanungstools spezialisierte Unternehmen mit aufgebaut. Als CTO ist er dort zuständig für den gesamten Entwicklungsbereich, beschäftigt sich mit der Produktentwicklung, Softwarearchitektur und -spezifikation, Technologieevaluierung, Prozess- und Teammanagement mit Ausrichtung auf die Microsoft .NET Plattform. In seiner Freizeit organisiert er die .NET User Group Berlin-Brandenburg und vermittelt sein Wissen auf Konferenzen, in User Groups, Onlinekursen, Fachartikeln und in seinem Blog (https://t.co/ZwA6qge61H). Er ist erreichbar via Twitter (https://t.co/rGn0fA41OT) und per E-Mail (robin.sedlaczek@live.de).', N'https://www.xing.com/img/users/3/5/e/f8445669a.5802118,4.256x256.jpg', NULL, NULL, N'Fairmas Gesellschaft für Marktanalysen mbH', N'https://robinsedlaczek.com/', NULL, N'RobinSedlaczek', N'https://www.xing.com/profile/Robin_Sedlaczek', NULL, NULL, CAST(N'2016-10-04T22:35:32.9800000+00:00' AS DateTimeOffset), CAST(N'2016-10-04T22:35:32.9839119+00:00' AS DateTimeOffset), 0)
GO
INSERT[dbo].[Speakers] ([Id], [FirstName], [LastName], [Biography], [PhotoUrl], [AvatarUrl], [PositionName], [CompanyName], [CompanyWebsiteUrl], [BlogUrl], [TwitterUrl], [LinkedInUrl], [Email], [RemoteId], [CreatedAt], [UpdatedAt], [Deleted]) VALUES(N'9CB05355-5667-40FB-9AC4-069012E33927', N'Carsten', N'König', N'Ich bin Carsten König, Diplom-Mathematiker, Softwareentwickler und beschäftige mich mit funktionaler Programmierung.
Ich arbeite seit ca. 10 Jahren als .NET-Softwareentwickler und programmiere schon seit meiner Schulzeit.Im Laufe der Jahre habe ich einige Programmiersprachen ausprobiert – von Basic über C/C++ bis Prolog. Besonders angetan haben es mir aber typisierte funktionale Programmiersprachen wie beispielsweise F#, daß ich begeistert in der Praxis einsetze.
Ich twittere als @CarstenK_Dev und man findet mich bei Google+. Außerdem blogge ich unter gettingsharper.de.', N'https://grossweber.com/assets/images/headshots/trainer/carsten-koenig-cb5855bd6ac.png', NULL, NULL, NULL, N'https://grossweber.com/ihre-vorteile/grossweber/prinzip-kluge-koepfe/#carsten-koenig', NULL, N'carstenk_dev', NULL, NULL, NULL, CAST(N'2016-10-04T22:35:32.9800000+00:00' AS DateTimeOffset), CAST(N'2016-10-05T20:30:48.0496374+00:00' AS DateTimeOffset), 0)
GO
INSERT [dbo].[Speakers] ([Id], [FirstName], [LastName], [Biography], [PhotoUrl], [AvatarUrl], [PositionName], [CompanyName], [CompanyWebsiteUrl], [BlogUrl], [TwitterUrl], [LinkedInUrl], [Email], [RemoteId], [CreatedAt], [UpdatedAt], [Deleted]) VALUES(N'A79D1597-2DAC-4BAF-A1D8-578201ED0648', N'Alexander', N'Witkowski', N'Ich bin Alexander Witkowski, Diplom-Ingenieur für Informationssystemtechnik und arbeite als Softwareentwickler hauptsächlich im Bereich mobiler Anwendungen und Hardware. Meine Schwerpunkte liegen bei den Universal Apps, der Windows Presentation Foundation, Windows Phone und Windows 10. In der Freizeit dreht sich bei mir alles darum, Hardware und Software miteinander zu verbinden. Ich habe meine Freude an LEGO seit meiner Kindheit nie abgelegt und ich finde, dass das Spiel eine grundlegende menschliche Aktivität ist, die Energie und Kraft freisetzt. Von einer LEGO Lunchbox, über einen LEGO USB-Stick bis hin zum LEGO Simpsons Haus – alles ist meins!
Während mein erstes Nokia-Handy noch kompliziert und kryptisch zu programmieren war, geht das heutzutage mit neueren Technologien und Frameworks wunderbar einfach von der Hand.Mich treibt an, zu verstehen, wie etwas funktioniert.So verbinde ich schon mal eine Windows App über RFID mit einem Türschloss und einem Kartenleser, nur um zu schauen, ob es grundsätzlich geht. Mittlerweile bin ich durch die hohe Innovationsgeschwindigkeit bei Hard- und Software längst bei den Themen wie „Internet of Things“ und „Smart Home“ angekommen.Mein Schreibtisch sieht mit Lötkolben und Platinen oft eher wie ein kleines Labor als der Arbeitsplatz eines Softwareentwicklers aus.
In meiner Freizeit halte ich in der Community viele Workshops, so etwa beim Mobile Camp in Dresden oder der Spartakiade in Berlin.Ich führe einen technischen Blog, in dem ich zu meinen Erfahrungen berichte und publiziere ebenso bei Twitter.Von Microsoft wurde ich 2016 mit dem „Microsoft Most Valuable Professional“-Award (MVP) für meine Community-Arbeit im Windows-Development-Umfeld ausgezeichnet.', N'https://grossweber.com/assets/images/headshots/trainer/alexander-witkowski-cbadb4cb543.png', NULL, NULL, N'Marktjagd GmbH', N'https://grossweber.com/ihre-vorteile/grossweber/prinzip-kluge-koepfe/#alexander-witkowski', NULL, N'alex_witkowski', N'https://www.xing.com/profile/Alexander_Witkowski', NULL, NULL, CAST(N'2016-10-04T22:35:32.9800000+00:00' AS DateTimeOffset), CAST(N'2016-10-05T20:31:36.0663970+00:00' AS DateTimeOffset), 0)
GO
INSERT [dbo].[Speakers] ([Id], [FirstName], [LastName], [Biography], [PhotoUrl], [AvatarUrl], [PositionName], [CompanyName], [CompanyWebsiteUrl], [BlogUrl], [TwitterUrl], [LinkedInUrl], [Email], [RemoteId], [CreatedAt], [UpdatedAt], [Deleted]) VALUES(N'AC78FA45-141A-4B41-9805-CA68B3E58B00', N'Thomas', N'Schissler', N'Thomas Schissler ist Bereichsleiter der Softwareentwicklung bei der artiso solutions GmbH. Er ist Gründer der .NET Developer Group Ulm und Oberschwaben. Im April 2014 wurde er zum siebten Mal in Folge zum Microsoft MVP für Visual Studio ALM berufen. Thomas Schissler beschäftigt sich seit dem Jahr 2001 intensiv mit der Softwareentwicklung auf Basis von .NET. Er hat sich auf die Themen Team Foundation Server, Moderne Architekturkonzepte, Prozessdesign und verschiedene Technologien im Umfeld von Microsoft .NET spezialisiert und zählt in diesen Bereichen zu den anerkannten Top-Experten. Als zertifizierter Professional SCRUM Trainer und Professional SCRUM-Master unterstützt Thomas Schissler Unternehmen bei der Einführung agiler Softwareentwicklungsmethoden und effektiver Prozesse mit Visual Studio. Hierbei kann er sich neben seinem theoretischen Wissen auf umfangreiche Praxiserfahrungen aus der Tätigkeit mit den eigenen Teams bei artiso sowie aus zahlreichen im Kundenauftrag durchgeführten Trainings, Consultings und Coachings on the job stützen.', N'https://www.xing.com/image/e_3_5_0c52dd4ff_6314465_3/thomas-schissler-foto.256x256.jpg', NULL, NULL, N'artiso', N'http://www.artiso.com/expertenblog/author/thomasschissler/', NULL, N'TSchissler', N'https://www.xing.com/profile/Thomas_Schissler', NULL, NULL, CAST(N'2016-10-04T22:35:32.9800000+00:00' AS DateTimeOffset), CAST(N'2016-10-05T21:12:06.6448705+00:00' AS DateTimeOffset), 0)
GO
INSERT[dbo].[Speakers] ([Id], [FirstName], [LastName], [Biography], [PhotoUrl], [AvatarUrl], [PositionName], [CompanyName], [CompanyWebsiteUrl], [BlogUrl], [TwitterUrl], [LinkedInUrl], [Email], [RemoteId], [CreatedAt], [UpdatedAt], [Deleted]) VALUES(N'CCD41039-8861-4ACD-97FA-F03862A556DA', N'Edna', N'Kropp', N'Dipl.-Informatikerin Schwerpunkt Usability und User Experience, Nebenfach Arbeits- und Organisationspsychologie, Grundstudium BWL, World Usability Day Berlin Organisationsteam', N'https://www.xing.com/img/users/e/3/8/5ad60fb06.6815630,6.256x256.jpg', NULL, NULL, N'akquinet tech@spree', NULL, NULL, NULL, N'https://www.xing.com/profile/Edna_Kropp', NULL, NULL, CAST(N'2016-10-04T22:35:32.9800000+00:00' AS DateTimeOffset), CAST(N'2016-10-05T20:36:44.0673802+00:00' AS DateTimeOffset), 0)
