INSERT[dbo].[Speakers] ([Id], [FirstName], [LastName], [Biography], [PhotoUrl], [AvatarUrl], [PositionName], [CompanyName], [CompanyWebsiteUrl], [BlogUrl], [TwitterUrl], [LinkedInUrl], [Email], [RemoteId], [CreatedAt], [UpdatedAt], [Deleted]) VALUES(N'DAC5A8C9-8E68-44C8-B71C-5A3030765A49', N'Frank', N'Pommerening', N'Frank Pommerening ist seit 2004 Softwareentwickler und -architekt im Bereich .NET-Technologien. Er beschäftigt sich u. a. seit mehreren Jahren mit PostSharp sowie verteilten Anwendungen. Aktuell ist er Senior Softwareentwickler bei der AXP Consulting GmbH und Co. KG in Leipzig.', N'https://www.xing.com/image/1_b_3_6f95a638e_9038563_5/frank-pommerening-foto.256x256.jpg', NULL, NULL, N'AXP Consulting GmbH & Co. KG', NULL, NULL, N'FPommerening', N'https://www.xing.com/profile/Frank_Pommerening3', NULL, NULL, CAST(N'2016-10-04T22:35:32.9800000+00:00' AS DateTimeOffset), CAST(N'2016-10-05T21:16:45.3959239+00:00' AS DateTimeOffset), 0)
